    private void limpar() {
        cbAltura.setText("");
        cbPeso.setText("");
        imc.setText("");
        imcc.setText("");
    }

    private void calcular() {

        // VERIFICA SE OS CAMPOS FORAM PREENCHIDOS 
        if (cbAltura.getText().isEmpty() || cbPeso.getText().isEmpty()) {

            JOptionPane.showMessageDialog(null, "Preencha todos os campos para continuar.");

        } else {

            // ESTANCIANDO A CLASSE DOS CALCULOS
            Calculo cal = new Calculo();

            // CONVERTENDO OS VALORES DIGITADOS PARA FLOAT
            float alt, pes;
            alt = Float.valueOf(cbAltura.getText());
            pes = Float.valueOf(cbPeso.getText());

            // SETANDO RESULTADO DO IMC
            imc.setText(String.valueOf(cal.calcularIMC(alt, pes)));

            // Mensagem sobre o IMC
            imcc.setText(cal.mensagem(cal.calcularIMC(alt, pes)));

        }
    }

